<script>
	$('.picContainer').mouseover(function(){
		$(this).find('.closePic').css('display','block');
	}).mouseout(function(){
		$(this).find('.closePic').css('display','none');
	});

	$('.closePic').click(function(){
		// 发送ajax请求删除数据库收藏
		var id=$(this).parent().prev('input').val();
		var obj=$(this);
		
		$.get('/Home/collection/delete/'+id,function(data){
			if(data){
				obj.parent().parent().remove();
			}else{
				alert('删除失败');
			}
		});
	});
</script>



