        <script>

            $('.radioDiv').children('div').children('ul').children('li').click(function(){
                $(this).siblings().removeClass('radioChecked').css('border','1px solid #DDDDDD').css('line-height','24px').css('padding','0 22px');
                $(this).addClass('radioChecked').css('border','2px solid #B4A078').css('line-height','22px').css('padding','0 21px');

                var result=checkRadio();
                if(result==false){
                  
                }else{
                    var specification=$('#specification').html();
                    var lev=null;
                    specification=eval("("+specification+")");
                    for(var i=0;i<specification.length;i++){
                        for(var j=0;j<specification[i].length-2;j++){
                          if(result[j]!=specification[i][j]){
                            lev=null;
                            break;
                          }else{
                            lev=i;
                          }
                        }
                        if(lev==null){
                          
                        }else{
                          var index=specification[lev].length-1;
                          $('#goodsStock').html(specification[lev][index]);
                          $('#goodsPrice').html(specification[lev][index-1]);
                          break;
                        }
                    }
                }
            });
          

            // 判断是否选择了全部属性
            function checkRadio()
            {
                var checkedAttr=Array();
                var count=0;
                $('.radioDiv').each(function(i){
                  $(this).children('div').children('ul').children('li').each(function(){
                    if($(this).hasClass('radioChecked')){
                      checkedAttr[checkedAttr.length]=$(this).html();
                    }
                  });
                  count +=1;
                });
                if(checkedAttr.length<count){
                  return false;
                }else{
                  return checkedAttr;
                }
            }



            // 减号
            function subGoodsCount(obj)
            {
                var value=$(obj).next('input').val();
                if(value>1){
                  $(obj).next('input').val(value-1);
                }
            }

            // 加号
            function addGoodsCount(obj)
            {
                var value=$(obj).prev('input').val();
                $(obj).prev('input').val(parseInt(value)+1);
            }

            // 加入购物车
            $('#addShop').click(function(){
                // 判断是否登录，没有登录则跳转到登录页面
                if(!$('#isUserLogin').html()){
                    window.location.href='/Home/toLogin';
                    return false;
                }

                var num=$('input[name=goodsCount]').val();
                var id=$('#hiddenGoodsId').val();
                $.get('/Home/addShop',{'num':num,'gid':id});
                $('#shopCount').html(parseInt($('#shopCount').html())+parseInt(num));
            });

            // 收藏
            $('#collect').click(function(){
                // 判断是否登录，没有登录则跳转到登录页面
                if(!$('#isUserLogin').html()){
                    window.location.href='/Home/toLogin';
                    return false;
                }

                if($(this).children('img').attr('value')==0){
                    $.get('/Home/collection/addCollect',{'id':$('#hiddenGoodsId').val()});
                    $(this).children('img').attr('src',"{{ asset('/Home/css/goods/聚收藏gift.png') }}").attr('value',1);
                }else{
                    $.get('/Home/collection/delete',{'id':$('#hiddenGoodsId').val()});
                    $(this).children('img').attr('src',"{{ asset('/Home/css/goods/聚收藏.png') }}").attr('value',0);
                }
                
            });
        </script>
















