				// 先关闭所有订单
				$('table[data-reactid=".0.2.0.0.0"]').css('display','none');

				// 打开状态为1订单
				$('table[status="4"]').css('display','block');

			}else{
				// $('table[status="1"]').css('display','none');
				$('table[data-reactid=".0.2.0.0.0"]').css('display','none');
				// 关闭订单大框
				// $('#j-allOrderPanel').css('display','none');

				// 提示框赋值
				$('.m-panel1').find('span[data-reactid=".0.2.1.0.1.1"]').html('待评价');

				// 打开没单提示
				$('.m-panel1').css('display','block');
				
			}

		}

	}

