$(document).on('getCartList', function(event, res){
if(!res.data) return;
var _cartGroupList = res.data.cartGroupList || [];
var _vliongoods =[];
var _360DSPGoods = [];
_cartGroupList.map(function(cartGroup) {
return cartGroup.cartItemList;
}).reduce(function(list1, list2) {
return list1.concat(list2);
}, []).forEach(function(cart){
_vliongoods.push('{0}:{1}:{2}:{3}'.format(cart.itemId, cart.cnt, cart.retailPrice, cart.actualPrice));
});
/*购物车*/
window._vlion = window._vlion ||[];
window._vlion.cart=window._vlion.cart ||[];
_vlion.cart['carts']= _vliongoods;
_vlion.cart['extend']="";
_vlion.cart['wp']="pc";
_vlion.cart['isLogin']=_islogin;
_vlion.cart['userId']=_userid;
window._vlion.push(['$delayLoad']); //延时启动
$(document).off('getCartList');
});
</script>
