<script>	
	// $(function(){
		var $subs = $("input[name='select[]']");
		// alert($subs.filter(":checked").length);
		if($subs.filter(":checked").length > 0){
			$('button[data-reactid=".0.0.0.1.1.0"]').attr('class','w-button w-button-xl w-button-primary');
		}else{
			$('button[data-reactid=".0.0.0.1.1.0"]').attr('class','w-button w-button-xl w-button-disabled');
		}


	// });
	





	// 多选
	$('.all').prev('input').click(function() { 
    	$('input[type="checkbox"]').prop("checked", this.checked); 
    	$checked = $(this).prop("checked");

   		var $subs = $("input[name='select[]']");
		// alert($subs.filter(":checked").length);
		if($subs.filter(":checked").length > 0){
			$('button[data-reactid=".0.0.0.1.1.0"]').attr('class','w-button w-button-xl w-button-primary');
		}else{
			$('button[data-reactid=".0.0.0.1.1.0"]').attr('class','w-button w-button-xl w-button-disabled');
		}
    	// alert($checked);
    	Checked($checked);
 	 }); 


   	// 单选
	 $("input[name='select[]']").click(function() { 
	    var $subs = $("input[name='select[]']"); 
	    $('.all').prev('input').prop("checked" , $subs.length == $subs.filter(":checked").length ? true :false); 
	    $checked = $(this).prop("checked");
	    // alert($checked);
	    $id = $(this).prop("value");

   		var $subs = $("input[name='select[]']");
		// alert($subs.filter(":checked").length);
		if($subs.filter(":checked").length > 0){
			$('button[data-reactid=".0.0.0.1.1.0"]').attr('class','w-button w-button-xl w-button-primary');

		}else{
			$('button[data-reactid=".0.0.0.1.1.0"]').attr('class','w-button w-button-xl w-button-disabled');
		}
	    
	    Checked($checked,$id);
	  });

	 function Checked(checked,id=0){
	 	$.ajaxSetup({
		    headers: {
		        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
		    }
		});

		$.ajax({
			url:'/Home/shop/select',
			type:'post',
			async:true,
			data:{checked:checked,id:id},
			dataType:'json',
			success:function(data)
			{	
				// alert(data);
				$('span[data-reactid=".0.0.0.1.2.0.0.1.1"]').html(data);
				$('span[data-reactid=".0.0.0.1.2.1.1.1"]').html(data);

			},

			error:function()
			{
				// alert('ajax请求失败');
			}
		});

	 }
	 





	// j-reduce less 减号事件
	$('.j-reduce.less').click(function(){

		$jian = $(this).next('input').val();

		$jian = parseFloat($jian);

		$jian-=1;

		$(this).next('input').val($jian);

		$(this).next('input').trigger("change");

	});	

	// 加号事件
	$('.j-add.more').click(function(){

		$jia = $(this).prev('input').val();
		$jia = parseFloat($jia);
		$jia += 1;
		$(this).prev('input').val($jia);

		$(this).prev('input').trigger("change");

	});



	function Change(a,id){
		// alert(id);
		if(isNaN($(a).val()) || $(a).val()<=1 ){
			$(a).prev('span').attr('class','j-reduce less z-dis');
			$(a).next('span').attr('class','j-reduce more');
			$(a).val(1);
		}else if($(a).val()>=50){
			$(a).next('span').attr('class','j-reduce more z-dis');
			$(a).prev('span').attr('class','j-reduce less');
			$(a).val(50);
		}

		if($(a).val()>1 && $(a).val()<50 ){
			$(a).prev('span').attr('class','j-reduce less');
			$(a).next('span').attr('class','j-reduce more');
		}

		
		// 小计
		$total = $(a).val() * $(a).parents('.item.item-2.w4').prev('.item.item-1.w3').find('.price.aprice').find('span[data-reactid=".0.0.0.0.1.1.$1.2:$c10441090.2:$0.1.1"]').html();

		$total = $total.toFixed(2);




		$.ajaxSetup({
		    headers: {
		        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
		    }
		});

		$.ajax({
			url:'/Home/shop/store',
			type:'post',
			async:true,
			data:{number:$(a).val(),total:$total,id:id},
			dataType:'json',
			success:function(data)
			{	
				// alert(data);总和
				$('span[data-reactid=".0.0.0.1.2.0.0.1.1"]').html(data.sum);
				$('span[data-reactid=".0.0.0.1.2.1.1.1"]').html(data.sum);
				// 插入小计
				$(a).parents('.item.item-2.w4').next('div').find('span[data-reactid=".0.0.0.0.1.1.$1.2:$c10441090.4.0.1"]').html(data.total);
				$(a).val(data.number);

				// echo json_encode(array('sum'=>$sum,'total'=>$data['total']));

			},

			error:function()
			{	
				// alert(id);
				// Change(a,id);
				
				// alert('ajax请求失败');
			}
		});


	}

	// 提交事件
	
	$('button').click(function(){
		var $subs = $("input[name='select[]']");
		if($subs.filter(":checked").length >= 1){
			$('form[data-reactid=".0.0.0.2"]').submit();
		}
	});
	

	// 单个删除
	$('.del').click(function(){
		// alert($(this).attr('value'));die();
		var $data = new Array();
		$data[0]= $(this).attr('value');
		// $(this).val();
		$(this).parents('.cart-item.f-cb.cart-item-last').remove();
		$(this).parents('.cart-item.f-cb.cart-item-dis').remove();

		Del($data);
	});


	// 批量删除
	$('.mgl30').click(function(){
		// alert(111);
		var $subs = $("input[name='select[]']");
		var $data = new Array();

		for (var i = 0; i < $subs.filter(":checked").length; i++) {
			var $che = $subs.filter(":checked")[i];
			var $id= $che.value;
			$data[i] = $id;
			// alert($id);
			$subs.filter(":checked").parents('.cart-item.f-cb.cart-item-last').remove();
			$subs.filter(":checked").parents('.cart-item.f-cb.cart-item-dis').remove();
		};

		
		Del($data);
		
		
		$('.all').prev('input').trigger("click");

		Checked(false);
	});

	function Del(arr){

		var $subs = $("input[name='select[]']");
		// alert($subs.length);
		
		if($subs.length < 1){
			$('div[data-reactid=".0.0.0.0.2"]').css('display','block');
			$('div[data-reactid=".0.0.0.1"]').css('display','none');
			$('div[data-reactid=".0.0.0.0.1"]').css('display','none');
		}

		// console.log(arr);
		// die();
		$.ajaxSetup({
			    headers: {
			        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
			    }
			});

		$.ajax({
			url:'/Home/shop/delete',
			type:'post',
			async:true,
			data:{arr},
			dataType:'json',
			success:function(data)
			{	
				// alert(data);


			},

			error:function()
			{
				// alert('ajax请求失败');
			}
		});
	}
</script>